void printlongestSubstring(char *str)
{
	
	int count = 0;
	int ans = 0;
	int max  = 0;
	int i =0;
	for(i=0; i<strlen(str); i++)
	{
		
		if(str[i] == '(')
		{
			count++;
		}else if(str[i]==')')
		{
			if(count<=0)
			{
				max = max>ans?max:ans;
				count=0;
				ans = 0;
			}else
			{
				count--;
				ans +=2;
			}
			
		}else
			continue;
		
		
	}
	
	max  =  max>ans?max:ans;
	
	printf("%s==>%d\n", str, max);
	
}



